  .link-title {
    position: relative;
    /* left: -19px; */
    display: inline-block;
    margin: 20px 0;
    font-size: 15px;
    padding: 0 30px 0 25px;
    height: 45px;
    line-height: 45px;
    border-radius: 0 35px 35px 0;
    background: #404040;
    color: #fff;
  border-left: 4px solid #4F46E4;
  }
  .row {
      display: flex;
      /* margin-right: -15px; */
      /* margin-left: -15px; */
      flex-wrap: wrap;
  }
  .friends {
  display: flex;
  flex-wrap: wrap;
  }
  .friends .a-friend {
      display: flex;
      margin: 10px 10px 0 0;
      text-decoration: none;
      font-weight: 300;
      box-shadow: 0 1px 2px rgba(0, 0, 0, 0.2);
      padding: 10px;
      transition: box-shadow 0.2s, transform 0.2s;
  }
  .friends .a-friend:hover {
      box-shadow: 0 1px 2px rgba(0, 0, 0, 0.5);
  }
  .friends .a-friend:active {
      transform: scale(0.95);
  }
  .friends .a-friend .blog-avatar {
      background-color: white;
      border-radius: 50%;
      width: 50px;
      height: 50px;
  }
  .friends .a-friend .text-container {
      margin-left: 10px;
  }
  .friends .a-friend .text-container .name {
      font-size: 14px;
  }
  .friends .a-friend .text-container .description {
      font-size: 12px;
      margin-top: 5px;
  }

  .text-white {
    --tw-text-opacity: 1;
    color: rgb(255 255 255/var(--tw-text-opacity));
  }
  .font-bold {
    font-weight: 700;
  }
  .leading-5 {
    line-height: 1.25rem;
  }
  .p-3 {
    padding: .75rem;
  }
  .bg-red-50 {
    --tw-bg-opacity: 1;
    background-color: rgb(253 242 242/var(--tw-bg-opacity));
  }
  .bg-red-500 {
    --tw-bg-opacity: 1;
    background-color: rgb(240 82 82/var(--tw-bg-opacity));
  }
